import React from "react";
import './Sidebar2.css';
import { useState,useEffect } from 'react';

const Sidebar=({ socket,roomname })=>{
  let users=useState([]);

  useEffect(()=>{
    socket.on('joinResponse',(data)=>users.push(data));
  },[socket,users]);

  //get the username using the current webpage url
  const URL=window.location.href;
  const username=URL.split("/")[5];

  //access only those users which are in the same group
  users=users.filter((user)=>user["roomname"] !== roomname);

  return(
      <div className="sidebar-container">
          <h1 className="app-name">EchoVerse</h1>
          <h3 className="sidebar-heading">{username}</h3>
          <h3 className="online-heading">Online Users</h3>
          <div className="online-users">
            {users.map((user)=>(
              <p key={user["socketID"]}>{user["username"]}</p>
            ))}
          </div>
      </div>
  )
}

export default Sidebar;
