import React from "react";
import './Body.css';
import { useNavigate } from 'react-router-dom';

const Body=({ messages, socket })=>{
  //get the username using the current webpage url
  const URL=window.location.href;
  const username=URL.slice(URL.indexOf("loggedin")+9);
  const navigate=useNavigate();

  const leaveChat=()=>{
    socket.emit('leaveResponse',{ username: username });
    navigate("/");
  }

  return(
    <div className='body-container'>
      <header className='room-header'>
          <p>Hangout with Colleagues</p>
          <button className='leave-chat' onClick={leaveChat}>
              LEAVE CHAT
          </button>
      </header>

      <div className="all-chats">

        {messages.map((message=>message["name"]===username ? (
        <div className='chat-container' key={message["id"]}>
            <p className='sender-name1'>You</p>
            <div className='message-sent'>
                <p>{message["text"]}</p>
            </div>
        </div>
        ) : (
          <div className='chat-container' key={message["id"]}>
            <p className="sender-name2">{message["name"]}</p>
            <div className='message-received'>
                <p>{message["text"]}</p>
            </div>
        </div>
        )))}
      </div>
    </div>
  )
}

export default Body;
