import React from "react";
import './Sidebar1.css';
import userIcon from '../user-icon.jpg'

const expressAppURL="http://localhost:3001";

const Sidebar1=()=>{
	//get the username using the current webpage url
  const URL=window.location.href;
  const username=URL.slice(URL.indexOf("loggedin")+9);

	return(
		<div className="sidebar-container">
          <h1 className="app-name">EchoVerse</h1>
          <h3 className="sidebar-heading">{username}</h3>
          <h3 className="profile-heading">Change Profile</h3>
          <div className="change-profile">
						<img className='user-icon' src={userIcon} alt='User Icon' />
            <p><a href={expressAppURL+"/change"}>Click here to change your profile</a></p>
          </div>
      </div>
	)
}

export default Sidebar1;
