import React from 'react';
import './User.css';
import { Link } from 'react-router-dom'
import { useState } from 'react';
import { Icon } from 'react-icons-kit';
import { eyeOff } from 'react-icons-kit/feather/eyeOff';
import { eye } from 'react-icons-kit/feather/eye'
const expressAppURL="http://localhost:3001";

const ExistingUser=()=>{
  const [password,setPassword] = useState("");
  const [type,setType] = useState('password');
  const [icon,setIcon] = useState(eyeOff);
  const toggle=()=>{
    if(type==='password'){
      setIcon(eye);
      setType('text');
    }else{
      setIcon(eyeOff);
      setType('password');
    }
  }
  return(
    <div className='container'>
        <h1 className='app-title'>EchoVerse</h1>
        <form action={expressAppURL} method='POST'>
        <fieldset className='input-field'>
            <legend>User Login</legend>
            <label htmlFor='user-name'>USERNAME: </label>
            <input id='user-name' type='text' name='username' placeholder='Enter username here...' />
            <br />
            <br />
            <label className='password-label' htmlFor='password'>PASSWORD: </label>
            <input id='password' type={type} name='password' placeholder='Enter password here...' onChange={(e) => setPassword(e.target.value)} value={password} />
            <span onClick={toggle}>
                <Icon className="icon" icon={icon} size={22}/>
            </span>
            <br />
            <br />
            <br />
            <input type="submit" value="LOGIN" />
            <br />
            <br />
            <p className='new-user'>New User? <Link to="new">Click here</Link></p>
        </fieldset>
        </form>
    </div>
  )
}

export default ExistingUser;
