import React from "react";
import './Body1.css';
import { useNavigate } from 'react-router-dom';
import { useState } from 'react';

const expressAppURL="http://localhost:3001";

const Body2=({ socket })=>{
  const [roomname,setRoomname]=useState("");
  //get the username using the current webpage url
  const URL=window.location.href;
  const username=URL.slice(URL.indexOf("loggedin")+9);
  const navigate=useNavigate();

  const logoutApp=()=>{
    socket.emit('leaveResponse',{ username: username });
    navigate("/");
  }

  const formSubmit=()=>{
    socket.emit('newRoom',{ id: socket.id, roomname: roomname })
  }

  return(
    <div className='body-container'>
      <header className='room-header'>
          <p>Create/Join a EchoRoom</p>
          <button className='logout-app' onClick={logoutApp}>
              LOGOUT
          </button>
      </header>

      <div className="all-forms">
      <form className='form' action={expressAppURL} method='POST' onSubmit={formSubmit}>
        <fieldset className='input-field'>
            <legend>User Login</legend>
            <label htmlFor='user-name'>ECHOROOM NAME: </label>
            <input className='input' id='room-name' type='text' name='roomname' placeholder='Enter EchoRoom name here...' onChange={(e)=>setRoomname(e.target.value)} value={roomname} />
            <br />
            <br />
            <br />
            <input className='submit' type="submit" value="LOGIN" />
        </fieldset>
        </form>
      </div>
    </div>
  )
}

export default Body2;
