import React from 'react';
import './User.css';
import { Link } from 'react-router-dom'
import {Icon} from 'react-icons-kit';
import {eyeOff} from 'react-icons-kit/feather/eyeOff';
import {eye} from 'react-icons-kit/feather/eye'

const [password, setPassword] = useState("");
const [type, setType] = useState('password');
const [icon, setIcon] = useState(eyeOff);

const toggle=()=>{
    if (type==='password'){
      setIcon(eye);
      setType('text')
    } else {
      setIcon(eyeOff)
      setType('password')
    }
}
const ExistingUser=()=>{
    return(
        <div className='container'>
            <h1 className='app-title'>EchoVerse</h1>
            <form action='' method='POST'>
            <fieldset className='input-field'>
                <legend>User Login</legend>
                <label htmlFor='user-name'>USERNAME: </label>
                <input id='user-name' type='text' name='username' placeholder='Enter username here...' />
                <br />
                <br />
                <label htmlFor='password'>PASSWORD: </label>
                <input id='password' type='password' name='password' placeholder='Enter password here...' />
                <br />
                <br />
                <br />
                <input type="submit" value="LOGIN" />
                <br />
                <br />
                <p className='new-user'>New User? <Link to="new">Click here</Link></p>
            </fieldset>
            </form>
        </div>
    )
}

export default ExistingUser;
