import React from 'react';
import './User.css';
import { useState } from 'react';
import {Icon} from 'react-icons-kit';
import {eyeOff} from 'react-icons-kit/feather/eyeOff';
import {eye} from 'react-icons-kit/feather/eye'

const NewUser=()=>{
    const [password, setPassword] = useState("");
    const [type, setType] = useState('password');
    const [icon, setIcon] = useState(eyeOff);
    const toggle=()=>{
        if(type==='password'){
        setIcon(eye);
        setType('text');
        }else{
        setIcon(eyeOff);
        setType('password');
        }
    }
    const confirmPassword=()=>{

    }
    return(
        <div className='container'>
            <h1 className='app-title'>EchoVerse</h1>
            <form action='' method='POST'>
            <fieldset className='input-field'>
                <legend>New User</legend>
                <label htmlFor='user-name'>USERNAME: </label>
                <input id='user-name' type='text' name='username' placeholder='Enter your username here...' />
                <br />
                <br />
                <label className='password-label' htmlFor='password'>PASSWORD: </label>
                <input id='password' type={type} name='password' placeholder='Enter your password here...' onChange={(e) => setPassword(e.target.value)} value={password} />
                <span onClick={toggle}>
                    <Icon className="icon" icon={icon} size={22}/>
                </span>
                <br />
                <br /> 
                <label htmlFor='re-password'>RE-ENTER PASSWORD:</label>
                <input id='re-password' type='password' placeholder='Re-enter the password...' />
                <br />
                <br />
                <br />
                <input type="submit" value="LOGIN" />
            </fieldset>
            </form>
        </div>
    )
}

export default NewUser;
