import React from "react";
import './Footer.css';
import { useState } from "react";

const Footer=({ socket })=>{
  //get the username using the current webpage url
  const URL=window.location.href;
  const username=URL.slice(URL.indexOf("loggedin")+9);
  const [message,setMessage]=useState("");
  const sendMessage=(e)=>{
      e.preventDefault();
      message.trim()
      socket.emit('message',{text: message, name: username, id: `${socket.id}${Math.random()}`});
      setMessage("");
  }
  return(
      <div className="footer-container">
          <form className="send-message" onSubmit={sendMessage}>
              <input className="message-field" type="text" placeholder="Enter message here..." value={message} onChange={(e)=>setMessage(e.target.value)} />
              <input className='send' type="submit" value="SEND" />
          </form>
      </div>
  )
}

export default Footer;
